<script>import { classNames, css } from './utils';
import Layout from './Layout.svelte';
let _class = '';
export { _class as class };
export let style = '';
export let alignContent = '';
export let alignItems = '';
export let justifySelf = '';
export let alignSelf = '';
export let autoCols = '';
export let autoRows = '';
export let autoFlow = '';
export let justifyContent = '';
export let gap = '';
export let colGap = gap;
export let rowGap = gap;
export let templateAreas = '';
export let templateCols = '';
export let templateRows = '';
export let area = '';
export let col = '';
export let colEnd = '';
export let colStart = '';
export let row = '';
export let rowEnd = '';
export let rowStart = '';
export let basis = '';
export let grow = '';
export let order = '';
export let shrink = '';
// export let flex: Properties['flex'] = '';
export let padding = '0';
export let margin = '0';
export let width = 'auto';
export let height = 'auto';
export let minWidth = undefined;
export let maxWidth = undefined;
export let minHeight = undefined;
export let maxHeight = undefined;
export let boxSizing = 'border-box';
export let inline = false;
export let reverse = false;
/**
 *
 * @deprecated
 */
export let use = () => { };
export let action = (node) => { };
$: _colGap = colGap || gap;
$: _rowGap = rowGap || gap;
$: _gap = `${_rowGap} ${_colGap}`;
</script>

<Layout
	class={classNames(_class, 'grid')}
	{inline}
	{reverse}
	{width}
	{height}
	{minWidth}
	{maxWidth}
	{minHeight}
	{maxHeight}
	{alignContent}
	{alignItems}
	{justifyContent}
	{colStart}
	{colEnd}
	{rowStart}
	{rowEnd}
	{col}
	{row}
	{area}
	{justifySelf}
	{alignSelf}
	{basis}
	{grow}
	{shrink}
	{order}
	{padding}
	{margin}
	{boxSizing}
	gap={_gap}
	style={css(style, {
		gridAutoColumns: autoCols,
		gridAutoRows: autoRows,
		gridAutoFlow: autoFlow,
		gridTemplateAreas: templateAreas,
		gridTemplateColumns: templateCols,
		gridTemplateRows: templateRows
	})}
	{use}
	{action}
>
	<slot />
</Layout>
