<script>import { classNames, css } from './utils';
let _class = '';
export { _class as class };
export let style = {};
export let justifyContent = '';
export let alignContent = '';
export let alignItems = '';
export let justifySelf = '';
export let alignSelf = '';
export let gap = '';
export let wrap = 'nowrap';
export let basis = '';
export let grow = '';
export let order = '';
export let shrink = '';
export let flex = 'auto';
export let area = '';
export let col = '';
export let colEnd = '';
export let colStart = '';
export let row = '';
export let rowEnd = '';
export let rowStart = '';
export let padding = '0';
export let margin = '0';
export let width = 'auto';
export let height = 'auto';
export let minWidth = undefined;
export let maxWidth = undefined;
export let minHeight = undefined;
export let maxHeight = undefined;
export let boxSizing = 'border-box';
export let inline = false;
export let reverse = false;
/**
 *
 * @deprecated
 */
export let use = (node) => { };
export let action = (node) => { };
</script>

<div
	use:use
	use:action
	class={classNames(_class, { inline, reverse }, 'layout')}
	style={css(style)}
	style:justify-content={justifyContent}
	style:align-content={alignContent}
	style:justify-self={justifySelf}
	style:align-items={alignItems}
	style:align-self={alignSelf}
	style:gap
	style:flex-wrap={wrap}
	style:flex-basis={basis}
	style:flex-grow={grow}
	style:flex-order={order}
	style:flex-shrink={shrink}
	style:grid-area={area}
	style:grid-column={col}
	style:grid-column-start={colStart}
	style:grid-column-end={colEnd}
	style:grid-row={row}
	style:grid-row-start={rowStart}
	style:grid-row-end={rowEnd}
	style:padding
	style:margin
	style:box-sizing={boxSizing}
	style:width
	style:height
	style:min-width={minWidth}
	style:max-width={maxWidth}
	style:min-height={minHeight}
	style:max-height={maxHeight}
>
	<slot />
</div>

<style>
	.layout.row,
	.layout.col {
		display: flex;
	}
	.layout.row.inline {
		display: inline-flex;
	}
	.layout.col.inline {
		display: inline-flex;
	}
	.layout.row {
		flex-direction: row;
	}
	.layout.row.reverse {
		flex-direction: row-reverse !important;
	}
	.layout.col {
		flex-direction: column;
	}
	.layout.col.reverse {
		flex-direction: column-reverse !important;
	}

	.layout.grid {
		display: grid;
	}
	.layout.grid.inline {
		display: inline-grid;
	}

	.layout.item {
		display: block;
	}
	.layout.items.inline {
		display: inline-block;
	}
</style>
